FUNCTION.COMMENT_FIRST_LINE:
function get_policy_assignment_users(
ip_flow_code            IN              VARCHAR2,
ip_stage_code           IN              VARCHAR2,
ip_pid                  IN              NUMBER,
ip_id                   IN              NUMBER
)  return varchar2
is
x_user_list varchar2(4000);
x_err_seq number;
begin
begin

ms_apps_mdf_errors_pkg.insert_log
(pn_error_handle_id      =&gt; ip_pid,
pn_error_sequence       =&gt; x_err_seq,
pc_error_type           =&gt; 'LOG',
pc_error_text           =&gt;    ' '
|| 'ms_dms_flow_utilities.get_policy_assignment_users ('
|| ' ip_flow_code  =&gt; ''' ||    ip_flow_code  ||''' ,'
|| ' ip_stage_code =&gt; ''' ||    ip_stage_code ||''' ,'
|| ' ip_pid        =&gt; '   ||    ip_pid        ||' ,'
|| ' ip_id         =&gt; '   ||    ip_id
||' ) '
);

SELECT wm_concat(M.user_group_users)
into x_user_list
FROM ms_dms_manage_policy_v M
WHERE M.process_instance_id =ip_pid
AND M.instance_id = ip_id ;

SELECT wm_concat(user_name)
into x_user_list
FROM( SELECT DISTINCT COLUMN_VALUE AS USER_NAME
FROM TABLE (ms_apps_utilities.parse_string
(  x_user_list , ','
)
)
) ;

exception
when others then
ms_apps_mdf_errors_pkg.insert_log
(pn_error_handle_id      =&gt; ip_pid,
pn_error_sequence       =&gt; x_err_seq,
pc_error_type           =&gt; 'LOG',
pc_error_text           =&gt;    'DMS x_user_list:'  ||x_user_list
);
end;
return x_user_list;
end;
