FUNCTION.COMMENT_FIRST_LINE:
FUNCTION  get_form_description(
ip_flow_code            IN              VARCHAR2,
ip_stage_code           IN              VARCHAR2,
ip_pid                  IN              NUMBER,
ip_id                   IN              NUMBER
)
return varchar2 is
lv_viewname     varchar2(100);
lv_description  clob;
lv_sqlstmt      varchar2(4000);
begin
lv_viewname := get_flow_view_name (ip_flow_code);
lv_sqlstmt := 'select description from ' || lv_viewname ||' where process_instance_id = '||ip_pid ||'
and instance_id = '||ip_id||' and rownum = 1';
execute immediate lv_sqlstmt into lv_description ;
return lv_description;
EXCEPTION
WHEN NO_DATA_FOUND
then
RETURN NULL;
end get_form_description;

FUNCTION.COMMENT:
