FUNCTION.COMMENT_FIRST_LINE:
function GET_FLOW_VIEW_NAME (PC_FLOW_CODE varchar2,
pc_view_type varchar2 default 'V' )
RETURN VARCHAR2
IS
lc_namespace         VARCHAR2 (100) := 'xmlns="http://www.metricstream.com/appstudio/mdf"';
lv_flow              XMLTYPE;
lv_view_name         varchar2 (400);
begin
dbms_output.put_line('2');
SELECT XMLTYPE (flow_xml)
INTO lv_flow
FROM ms_apps_mdf_flow_xml
where flow_code = pc_flow_code;
dbms_output.put_line('1');
if pc_view_type='V' then
dbms_output.put_line('Anil-inside if');
SELECT stages.EXTRACT ('stage/view/text()', lc_namespace).getstringval() view_name
INTO lv_view_name
FROM TABLE
(XMLSEQUENCE
((SELECT lv_flow.EXTRACT
('process/stages/stage[enabled="Y"]',
lc_namespace
)
FROM DUAL)
)
) stages
WHERE ROWNUM = 1;
else
dbms_output.put_line('Anil-inside else');
SELECT stages.EXTRACT ('stage/displayview/text()', lc_namespace).getstringval() view_name
INTO lv_view_name
FROM TABLE
(XMLSEQUENCE
((SELECT lv_flow.EXTRACT
('process/stages/stage[enabled="Y"]',
lc_namespace
)
FROM DUAL)
)
) stages
WHERE ROWNUM = 1;
end if;
dbms_output.put_line('Anil-End  : ' || lv_view_name);
RETURN lv_view_name;
EXCEPTION
WHEN NO_DATA_FOUND
then
return null;
end get_flow_view_name;

