FUNCTION.COMMENT_FIRST_LINE:
Procedure prc_process_sleep
As
l_min_session_sleep   NUMBER := 0;
l_max_session_sleep   NUMBER := 0;
l_max_session         NUMBER := 0;
l_max_sess_ct         NUMBER := 0;
BEGIN
SELECT parameter_value
INTO l_max_session
FROM ms_ism_throttling
WHERE parameter_name = 'MAX_ALLOWED_SESSIONS';
SELECT parameter_value
INTO l_min_session_sleep
FROM ms_ism_throttling
WHERE parameter_name = 'MIN_SESSION_SLEEP_TIME';
SELECT parameter_value
INTO l_max_session_sleep
FROM ms_ism_throttling
WHERE parameter_name = 'MAX_SESSION_SLEEP_TIME';
LOOP
SELECT COUNT (1)
INTO l_max_sess_ct
FROM v$session
WHERE status = 'ACTIVE'
AND TYPE != 'BACKGROUND'
AND event != 'PL/SQL lock timer';
EXIT WHEN (l_max_sess_ct &lt;= l_max_session);
DBMS_LOCK.sleep (DBMS_RANDOM.VALUE (l_min_session_sleep, l_max_session_sleep));
END LOOP;
EXCEPTION WHEN OTHERS THEN
NULL;
End prc_process_sleep;

