FUNCTION ms_Rsk_Qnt_Get_ObjectId( pv_object_id IN VARCHAR2) RETURN VARCHAR2 IS
lv_result     VARCHAR2(100);
lv_error_code VARCHAR2(100);
BEGIN
SELECT distinct object_id
INTO lv_result
FROM ms_rsk_quant_assmnt_factor_v --ms_rsk_qnt_asmt_fctr
WHERE object_id=pv_object_id;
RETURN lv_result;
EXCEPTION
WHEN OTHERS THEN
RETURN NULL;
END ms_Rsk_Qnt_Get_ObjectId;

