function GET_OWNER_ORG(P_PERSPECTIVE_ID in varchar2)
return varchar2
as
l_org varchar2(4000);
begin
select MS_CONCAT(OWNER_ORGANIZATION)
into l_org
from MS_RSK_SCENARIOS_ORN
where SCENARIO_ID in (P_PERSPECTIVE_ID);
return L_ORG;
EXCEPTION
when OTHERS then
return null;
end;
