L_ORG varchar2(4000);
begin

select MS_CONCAT(ORGANIZATION_ID)
INTO L_ORG
from MS_RSK_ORGANIZATION_ROLLUP
where SCENARIO_ID in (P_PERSPECTIVE_ID);
return L_ORG;
EXCEPTION
when OTHERS then
return null;
end;

