PROCEDURE.COMMENT_FIRST_LINE:
FUNCTION fn_ins_a_row_to_assment_v(p_object_id         ms_rsk_risk_assessment_v.object_id%TYPE,
p_process_instance_id ms_rsk_risk_assessment_v.process_instance_id%TYPE)
RETURN BOOLEAN IS
lv_proc VARCHAR2(100) := 'Assessment_view insertion function';
lv_instance_id  ms_rsk_risk_assessment_v.instance_id%TYPE;
BEGIN
debug( Lv_Proc||'Insert into assessment temp table table');
SELECT si_metric_results_s.NEXTVAL INTO lv_instance_id FROM dual;
debug( Lv_Proc||'Insert into assessment temp table table');
INSERT INTO ms_rsk_risk_assess_temp_v
SELECT *
FROM ms_rsk_risk_assessment_v
WHERE process_instance_id = p_process_instance_id
AND object_id = p_object_id
AND instance_id = (SELECT MAX(instance_id) FROM ms_rsk_risk_assessment_v WHERE process_instance_id = p_process_instance_id
AND object_id = p_object_id)
;
debug( Lv_Proc||'update assessment temp table table');
UPDATE ms_rsk_risk_assess_temp_v
SET (previous_stage, dd_current_stage,
object_action, progress_status,
dd_current_user_name, dd_event_user_name,
instance_id, instance_rec_num,
process_instance_id) = (SELECT 0,'CREATE_EDIT',
'','Assess Assessment',
'','',
lv_instance_id,1,
p_process_instance_id
FROM dual)
WHERE process_instance_id = p_process_instance_id
AND object_id = p_object_id
AND instance_id  =(SELECT MAX(instance_id) FROM ms_rsk_risk_assessment_v WHERE process_instance_id = p_process_instance_id
AND object_id = p_object_id);
debug( Lv_Proc||'Insert into assessment table table');
INSERT INTO ms_rsk_risk_assessment_v
SELECT *
FROM ms_rsk_risk_assess_temp_v
WHERE process_instance_id = p_process_instance_id
AND object_id = p_object_id
AND instance_id = lv_instance_id;
COMMIT;
RETURN TRUE;
EXCEPTION
WHEN OTHERS THEN
RETURN FALSE;
END;

