FUNCTION.COMMENT_FIRST_LINE:
FUNCTION get_risk_cat (i_risk_cat VARCHAR2)
RETURN VARCHAR2
IS
x_fact_id   VARCHAR2 (4000);
CURSOR cur_fact_details
IS
SELECT risk_category
FROM ms_rsk_qualt_assmnt_fa
WHERE risk_category = i_risk_cat;
BEGIN
FOR c_fact_details IN cur_fact_details
LOOP
IF x_fact_id IS NULL
THEN
x_fact_id := c_fact_details.risk_category;
ELSE
x_fact_id := x_fact_id || ',' || c_fact_details.risk_category;
END IF;
END LOOP;
RETURN x_fact_id;
EXCEPTION
WHEN OTHERS
THEN
RETURN NULL;
END get_risk_cat;

FUNCTION.COMMENT:
FUNCTION get_risk_cat (i_risk_cat VARCHAR2)
RETURN VARCHAR2
IS
x_fact_id   VARCHAR2 (4000);
CURSOR cur_fact_details
IS
SELECT risk_category
FROM ms_rsk_qualt_assmnt_fa
WHERE risk_category = i_risk_cat;
BEGIN
FOR c_fact_details IN cur_fact_details
LOOP
IF x_fact_id IS NULL
THEN
x_fact_id := c_fact_details.risk_category;
ELSE
x_fact_id := x_fact_id || ',' || c_fact_details.risk_category;
END IF;
END LOOP;
RETURN x_fact_id;
EXCEPTION
WHEN OTHERS
THEN
RETURN NULL;
END get_risk_cat;

PROCEDURE.COMMENT_FIRST_LINE:
SELECT column_value
BULK COLLECT INTO lv_main_list
FROM TABLE(MS_APPS_UTILITIES.PARSE_STRING(ip_main_string,','));
dbms_output.put_line('lv_main_list.COUNT '||lv_main_list.COUNT);
IF lv_main_list.COUNT &gt; 0
THEN
dbms_output.put_line('lv_query_list.COUNT '||lv_query_list.COUNT);
IF lv_query_list.COUNT &gt; 0
THEN
FOR i IN 1..lv_query_list.COUNT
LOOP
FOR j IN 1..lv_main_list.COUNT
LOOP
dbms_output.put_line('lv_query_list(i) '||lv_query_list(i));
dbms_output.put_line('lv_main_list(j) '||lv_main_list(j));
IF lv_query_list(i) = lv_main_list(j)
THEN
RETURN 1;
END IF;
END LOOP;
END LOOP;
END IF;
END IF;

