FUNCTION.COMMENT_FIRST_LINE:
function get_last_assessed_by(object_id varchar2) return varchar2 is
lv_rsk_prv_assessor varchar2(4000) := null;
l_prev_vers_no number;
begin
select max(version_no) into l_prev_vers_no from ms_rsk_ongoing_asssmnt_trend where assmnt_id=:object_id;
select last_assessed_by  into lv_rsk_prv_assessor from
ms_rsk_ongoing_asssmnt_trend where assmnt_id=:object_id and version_no=l_prev_vers_no - 1;
if lv_rsk_prv_assessor is not null then
return  lv_rsk_prv_assessor;
else
return null;
END IF;
exception
when others then
return null;
end get_qual_risk_category;

